export const createOffChainCancelAction = (signer, orderHash, seaportContractAddress, chainId) => {
    // EIP-712 data
    const domain = {
        name: 'Seaport',
        version: '1.6',
        chainId: chainId,
        verifyingContract: seaportContractAddress,
    };
    const types = {
        OrderHash: [{ name: 'orderHash', type: 'bytes32' }],
    };
    const value = {
        orderHash: orderHash,
    };
    return {
        type: 'offChainCancel',
        getMessageToSign: () => orderHash,
        createCancelSignature: async () => {
            // Sign the EIP-712 data
            const signature = await signer.signTypedData(domain, types, value);
            return {
                orderHash,
                signature,
            };
        },
    };
};
